  `md5` varchar(32) DEFAULT NULL COMMENT &amp;#39;md5&amp;#39;,
  `gmt_create` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT &amp;#39;åˆ›å»ºæ—¶é—´&amp;#39;,
