  KEY `idx_gmt_modified` (`gmt_modified`),
  KEY `idx_did` (`data_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT=&amp;#39;å¤šç§Ÿæˆ·æ”¹é€ &amp;#39;;


