CREATE TABLE `tenant_info` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT &amp;#39;id&amp;#39;,
  `kp` varchar(128) NOT NULL COMMENT &amp;#39;kp&amp;#39;,
