&lt;h5 id=&#34;23invalidatehttpsessionboolean&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#23invalidatehttpsessionboolean&#34;&gt;#&lt;/a&gt; 2.3invalidateHttpSession(boolean)&lt;/h5&gt;
